public class Stack {

  private Node top;

  public Stack(){
    this.top = null;
  }

  private class Node{
    int data;
    Node next;

    public Node(int data){
      this.data = data;
      this.next = null;
    }
  }

  public void push(int data){
    Node newNode = new Node(data);
    if(top == null){
      top = newNode;
    } else {
      newNode.next = top;
      top = newNode;
    }
  }

  public int pop(){
    if(top == null){
      throw new NoSuchElementException("Stack Underflow Error");
    }
    int data = top.data;
    top = top.next;
    return data;
  }

  public int peek(){
    if(top == null){
      throw new NoSuchElementException("Stack Underflow Error");
    }
    return top.data;
  }

  public boolean isEmpty(){
    return top == null;
  }
}

