function isBalanced(s) {
    let stack = [];
    let open = { '{': '}', '[': ']', '(': ')' };
    let closed = { '}': true, ']': true, ')': true };

    for (let i = 0; i < s.length; i++) {
      let chr = s[i];
      if (open[chr]) {
        stack.push(chr);
      } else if (closed[chr]) {
        if (!stack.length) {
          return 'NOT BALANCED';
        }
        let last = stack.pop();
        if (open[last] !== chr) {
          return 'NOT BALANCED';
        }
      }
    }
    return stack.length ? 'NOT BALANCED' : 'BALANCED';
  }

