    if len(str1) != len(str2):
        return False

    freq1 = {}
    freq2 = {}

    for char in str1:
        if char not in freq1:
            freq1[char] = 1
        else:
            freq1[char] += 1

    for char in str2:
        if char not in freq2:
            freq2[char] = 1
        else:
            freq2[char] += 1

    for key in freq1:
        if key not in freq2 or freq2[key] != freq1[key]:
            return False

    return True

